import React from 'react';
import Main from "./content/Main/Main";
import { Switch, Route} from 'react-router-dom'
import CurrentMusic from './content/CurrentMusic/CurrentMusic'
import SignUp from './content/Sign Up/SignUp'
import LogIn from './content/Log In/LogIn'
import UploadMusic from './content/UploadMusic/UploadMusic';


class App extends React.Component{
    state = { // State
      songs: [
          {name: 'song 1', duration: '2:32', fileName: '../StaticMusic/first.mp3'},
          {name: 'song 2', duration: '1:56', fileName: '../StaticMusic/second.mp3'},
          {name: 'song 3', duration: '3:23', fileName: '../StaticMusic/third.mp3'},
          {name: 'song 4', duration: '2:20', fileName: '../StaticMusic/second.mp3'},
          {name: 'song 5', duration: '1:23', fileName: '../StaticMusic/first.mp3'}
      ]
  }
  render(){
  return (
    <>
      <Switch>
        <Route exact path="/">
          <Main />
        </Route>
        <Route path="/current" >
          <CurrentMusic songname={this.state.songs[_].name} songduration={this.state.songs} songfile={this.state.songs}/>
        </Route>
        <Route path="/signup" component={SignUp} />
        <Route path="/signin"  component={LogIn} />
        <Route path="/upload" component={UploadMusic} />
      </Switch>
    </>
  )
  }
}

export default App;
