import React from "react";
import './Main.css'
import {NavLink} from 'react-router-dom'
import "../StaticMusic/";


class Main extends React.Component{
    constructor(props){
        super(props)

        this.state = { // State
            songs: [
                {name: 'song 1', duration: '2:32'},
                {name: 'song 2', duration: '1:56'},
                {name: 'song 3', duration: '3:23'},
                {name: 'song 4', duration: '2:20'},
                {name: 'song 5', duration: '1:23'}
            ],
            files: [
                {fileName: '../StaticMusic/first.mp3'},
                {fileName: '../StaticMusic/second.mp3'},
                {fileName: '../StaticMusic/third.mp3'},
                {fileName: '../StaticMusic/fourth.mp3'},
                {fileName: '../StaticMusic/fifth.mp3'}
            ]
        }
    }


    render(){

        

        return(

            <div className="main">
    
                <header>
                    <div className="logo">#</div>
    
                    <div className="buttonHeader">
                        <button className="reg"><NavLink style={{textDecoration: 'none'}} to="/signup">SIGN UP</NavLink></button>
                        <button className="sign" ><NavLink style={{textDecoration: 'none', color: 'white'}} to="/signin">SIGN IN</NavLink></button>
                    </div>
                </header>
                <div style={{flexDirection: 'column', alignItems: 'center', justifyContent: 'center'}}>
                    <h1 style={{marginBottom: '10px'}}>We recommend to listen</h1>
    
                    <div className="Box">
    
                    {this.state.songs.map((song, index) => ( // Ітерирую state і вивожу 5 разів з унікальним ім'ям , id, тривалістю
                        <div className="Box1" key={index}>
                            <p className="Song4">{song.name}</p>
                                <div>
                                    <p className="Time">{song.duration}</p>
                                    <NavLink to="/current"><img onClick={
                                        () => {
                                             localStorage.setItem('song', song.name);
                                             localStorage.setItem('duration', song.duration)
                                            }
                                        } style={{marginTop: '5px'}} alt={"#"} src="https://img.icons8.com/material-outlined/1x/circled-play.png" className="pause4"/></NavLink>
                                </div>
                        </div>
                    ))}
    
 
                    </div>
    
                </div>
                <div className="footer_text">
                <footer>
                    <p >Copyright © , 2020, NAME.</p>
                </footer>
            </div>
            </div>
        )
    }
}

export default Main
